import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={() => alert("" + this.props.row + ", " + this.props.col)}/>
        );
    }
}

class Board extends React.Component {
    render() {
        var cells = [];
        for(var i = 0; i < 9; i++)
        {
            cells[i] = new Array(9);
        }
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                cells[k][j] = <Cell row={k} col={j}/>;
            }
        }
        return (
            <div>
                <h2>Minesweeper.React</h2>
                <div className="grid">
                    {cells}
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
