import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={() => alert("" + this.props.row + ", " + this.props.col)}/>
        );
    }
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cells: this.arr,
        }
    }
    render() {
        var arr = [];
        for(var i = 0; i < 9; i++)
        {
            arr[i] = new Array(9);
        }
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                arr[k][j] = <Cell key={k + j} row={k} col={j}/>;
            }
        }


        this.state.cells[0][0].col = 100;
        this.forceUpdate();
        // var count = 0;
        // while(count < 10)
        // {
        //     var rand_r = Math.floor(Math.random() * 9);
        //     var rand_c = Math.floor(Math.random() * 9);
        //     cells[rand_r][rand_c].setState = 'bomb';
        //     count++;
        // }

        return (
            <div>
                <h2>Minesweeper.React</h2>
                <div className="grid">
                    {cells}
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
