import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Cell(props) {
        return (
            <p className="cell" onClick={props.onClick} onContextMenu={props.onClick}>
                {props.face}
            </p>
        );
}

function init_board(arr) {
    var count = 0;
    while(count < 10)
    {
        var rand_r = Math.floor(Math.random() * 9);
        var rand_c = Math.floor(Math.random() * 9);
        while(arr[rand_r][rand_c] === '*')
        {
            rand_r = Math.floor(Math.random() * 9);
            rand_c = Math.floor(Math.random() * 9);
        }
        arr[rand_r][rand_c] = '*';
        count++;
    }

    var neighbor_rows = [-1, -1, -1, 0, 0,   1, 1, 1];
    var neighbor_cols = [-1,  0,  1, -1, 1, -1, 0, 1];

    for(var i = 0; i < 9; i++)
    {
        for(var j = 0; j < 9; j++)
        {
            if(arr[i][j] !== '*')
            {
                var cnt_mines = 0;
                for(var k = 0; k < 9; k++)
                {
                    var row_to_check = i + neighbor_rows[k];
                    var col_to_check = j + neighbor_cols[k];
                    if(row_to_check < 0 || row_to_check > 9 || col_to_check < 0 || col_to_check > 9)
                    {
                        continue;
                    }
                    else
                    {
                        try {
                            if(arr[row_to_check][col_to_check] === '*')
                                {
                                    cnt_mines++;
                                }
                        } catch (error) {
                            //some NaN/undefined error we can ignore
                        }
                    }
                }
                arr[i][j] = cnt_mines;
            }
        }
    }
    return arr;
}

var bool_arr = [];
var val_arr = [];
var flag_arr = [];
var val_arr_isInitialized = false;

for(var i = 0; i < 9; i++)
{
    bool_arr[i] = new Array(9);
    val_arr[i] = new Array(9);
    flag_arr[i] = new Array(9);
}

class Board extends React.Component {
    constructor(props) {
        super(props);

        var init = [];
        for(var i = 0; i < 9; i++)
        {
            init[i] = new Array(9);
        }
        init_board(init);

        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                var val = init[k][j];
                init[k][j] = this.renderCell(k, j, val, "");
            }
        }

        this.state = {
            cells: init,
        }
    }

    reveal(row, col) {
        if(val_arr[row][col] === 0 && !bool_arr[row][col])
        {
            bool_arr[row][col] = true;

            var neighbor_rows = [-1, -1, -1, 0,  0,  1, 1, 1];
            var neighbor_cols = [-1,  0,  1, -1, 1, -1, 0, 1];

            for(var i = 0; i < 9; i++)
            {
                var row_to_check = row + neighbor_rows[i];
                var col_to_check = col + neighbor_cols[i];

                if(row_to_check < 0 || row_to_check >= 9 || col_to_check < 0 || col_to_check >= 9)
                    continue;

                if(isNaN(row_to_check) || isNaN(col_to_check))
                    break;

                this.reveal(row_to_check, col_to_check);
            }
        }
        else if(flag_arr[row][col])
        {
            //do nothing
        }
        else
        {
            bool_arr[row][col] = true;
        }
    }

    flag(row, col) {
        if(!bool_arr[row][col] || flag_arr[row][col])
        {
            // bool_arr[row][col] = true;
            flag_arr[row][col] = !flag_arr[row][col];
        }
    }

    handleClick(row, col, event) {
        event.preventDefault(); //need this so menu doesn't pop up when we right click

        var cells_copy = [];
        for(var i = 0; i < 9; i++)
        {
            cells_copy[i] = new Array(9);
        }

        if(!val_arr_isInitialized) {
            for(var j = 0; j < 9; j++)
            {
                for(var k = 0; k < 9; k++)
                {
                    this.state.cells[j][k].props.face === "" ? bool_arr[j][k] = false : bool_arr[j][k] = true;
                    val_arr[j][k] = this.state.cells[j][k].props.value;
                    flag_arr[j][k] = false;
                }
            }
            val_arr_isInitialized = true;
        }

        if(event.type === 'click')
            this.reveal(row, col);
        else
        {
            this.flag(row, col);
        }

        for(var r = 0; r < 9; r++)
        {
            for(var c = 0; c < 9; c++)
            {
                if(bool_arr[r][c])
                {
                    cells_copy[r][c] = this.renderCell(r, c, (this.state.cells[r][c].props.value), (this.state.cells[r][c].props.value));
                }
                else if(flag_arr[r][c])
                {
                    cells_copy[r][c] = this.renderCell(r, c, (this.state.cells[r][c].props.value), "M");
                }
                else
                {
                    cells_copy[r][c] = this.renderCell(r, c, (this.state.cells[r][c].props.value), "");
                }
            }
        }

        this.setState({cells: cells_copy});
    }

    renderCell(r, c, v, f) {
        return <Cell row={r} col={c} value={v} face={f} onClick={(event) => this.handleClick(r, c, event)}/>
    }

    render() {
        if (this.state.init === null) return null;
        const num_mines = 10;
        return (
            <div>
                <div>
                    <h2># of mines: {num_mines}</h2>
                </div>
                <div className="grid">
                    {this.state.cells}
                </div>
            </div>
        )
    }
}

class Game extends React.Component {
    render() {
        return(
            <div>
                <h2>React.Minesweeper</h2>
                <Board />
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
