import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            row: this.props.row,
            col: this.props.col,
            value: this.props.value,
            face: this.props.face,
        }
    }

    lost() {
        alert("Kaboom! You lose :(");
    }

    onCellClick() {
        // this.state.value === "bomb" ? this.setState({face: '*'}) : this.setState({face: this.state.value})
        if(this.state.value === "bomb")
        {
            this.setState({face: '*'}, this.lost);
        }
        else
        {
            this.setState({face: this.state.value});
        }
    }

    render() {
        return (
            <button className="cell" onClick={() => this.onCellClick()}>
                {this.state.face}
            </button>
        );
    }
}

function init_board(arr) {
    var count = 0;
    while(count < 10)
    {
        var rand_r = Math.floor(Math.random() * 9);
        var rand_c = Math.floor(Math.random() * 9);
        while(arr[rand_r][rand_c] === 'bomb')
        {
            rand_r = Math.floor(Math.random() * 9);
            rand_c = Math.floor(Math.random() * 9);
        }
        arr[rand_r][rand_c] = 'bomb';
        count++;
    }

    var neighbor_rows = [-1, -1, -1, 0, 0,   1, 1, 1];
    var neighbor_cols = [-1,  0,  1, -1, 1, -1, 0, 1];

    for(var i = 0; i < 9; i++)
    {
        for(var j = 0; j < 9; j++)
        {
            if(arr[i][j] !== 'bomb')
            {
                var cnt_mines = 0;
                for(var k = 0; k < 9; k++)
                {
                    var row_to_check = i + neighbor_rows[k];
                    var col_to_check = j + neighbor_cols[k];
                    if(row_to_check < 0 || row_to_check > 9 || col_to_check < 0 || col_to_check > 9)
                    {
                        continue;
                    }
                    else
                    {
                        try {
                            if(arr[row_to_check][col_to_check] === 'bomb')
                                {
                                    cnt_mines++;
                                }
                        } catch (error) {
                            //some NaN/undefined error we can ignore
                        }
                    }
                }
                arr[i][j] = cnt_mines;
            }
        }
    }
    return arr;
}

class Board extends React.Component {
    constructor(props) {
        super(props);

        var init = [];
        for(var i = 0; i < 9; i++)
        {
            init[i] = new Array(9);
        }
        init_board(init);
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                var val = init[k][j];
                init[k][j] = <Cell row={k} col={j} value={val} face={null}/>;
            }
        }

        this.state = {
            cells: init,
        }
    }

    handleClick(row, col, value) {
        // alert("" + row + " " + col + " " + value);
        var revealed_cells = [];
        for(var i = 0; i < 9; i++)
        {
            revealed_cells[i] = new Array(9);
        }
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                if(k === row && j === col)
                {
                    var face = value;
                    if(face === 'bomb')
                    {
                        face = '*';
                    }
                    revealed_cells[k][j] = this.renderCell(row, col, value, face);
                }
                else
                {
                    revealed_cells[k][j] = this.state.cells[k][j];
                }
            }
        }

        this.setState({cells: revealed_cells});
    }

    renderCell(r, c, v, f) {
        return <Cell row={r} col={c} value={v} face={f}/>
    }

    render() {

        // var init = [];
        // for(var i = 0; i < 9; i++)
        // {
        //     init[i] = new Array(9);
        // }
        // init_board(init);
        // for(var k = 0; k < 9; k++)
        // {
        //     for(var j = 0; j < 9; j++)
        //     {
        //         var val = init[k][j];
        //         init[k][j] = <Cell key={k + 1} row={k} col={j} value={val} face={null}/>;
        //     }
        // }

        return (
            <div>
                <h2>Minesweeper.React</h2>
                <div className="grid">
                    {this.state.cells}
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
