import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={() => alert("Hi")}/>
        );
    }
}

class Board extends React.Component {
    renderRow() {
        var cells = [];
        for(var i = 0; i < 9; i++)
        {
            cells.push(Cell());
        }
        return <div className="board-row">{cells}</div>
    }

    render() {
        return (
            <div>
                <h2>React.Minesweeper</h2>
                {this.renderRow}
                {this.renderRow}
                {this.renderRow}
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
