import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={() => alert("Hi")}/>
        );
    }
}

class Board extends React.Component {
    render() {
        var cells = [];
        for(var i = 0; i < 9; i++)
        {
            cells[i] = new Array(9);
        }
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                if(j == 0)
                {
                    cells[k][j] = (<div className="board-row"><Cell /></div>);
                }
                cells[k][j] = <Cell />;
            }
        }
        return (
            <div>
                <h2>React.Minesweeper</h2>
                {cells}
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
