import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={() => alert("Hi")}/>
        );
    }
}

class Board extends React.Component {
    constructor(props) {
        super(props);
    }

    renderRow() {
        return (
            <div className="board-row">
                <Cell />
                <Cell />
                <Cell />
            </div>
        );
    }

    render() {
        return (
            <div>
                <h2>React.Minesweeper</h2>
                {this.renderRow()}
                <div className="board-row">
                    <Cell />
                    <Cell />
                    <Cell />
                </div>
                <div className="board-row">
                    <Cell />
                    <Cell />
                    <Cell />
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
