import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Cell(props) {
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         row: this.props.row,
    //         col: this.props.col,
    //         value: this.props.value,
    //         face: this.props.face,
    //     }
    // }

    // onCellClick() {
    //     // this.state.value === "bomb" ? this.setState({face: '*'}) : this.setState({face: this.state.value})
    //     if(this.state.value === "bomb")
    //     {
    //         this.setState({face: '*'});
    //     }
    //     else
    //     {
    //         this.setState({face: this.state.value});
    //     }
    // }

    // render() {
        return (
            // <button className="cell" onClick={() => this.onCellClick()}>
            //     {this.state.face}
            // </button>
            <button className="cell" onClick={props.onClick}>
                {props.face}
            </button>
        );
    // }
}

function init_board(arr) {
    var count = 0;
    while(count < 10)
    {
        var rand_r = Math.floor(Math.random() * 9);
        var rand_c = Math.floor(Math.random() * 9);
        while(arr[rand_r][rand_c] === 'bomb')
        {
            rand_r = Math.floor(Math.random() * 9);
            rand_c = Math.floor(Math.random() * 9);
        }
        arr[rand_r][rand_c] = 'bomb';
        count++;
    }

    var neighbor_rows = [-1, -1, -1, 0, 0,   1, 1, 1];
    var neighbor_cols = [-1,  0,  1, -1, 1, -1, 0, 1];

    for(var i = 0; i < 9; i++)
    {
        for(var j = 0; j < 9; j++)
        {
            if(arr[i][j] !== 'bomb')
            {
                var cnt_mines = 0;
                for(var k = 0; k < 9; k++)
                {
                    var row_to_check = i + neighbor_rows[k];
                    var col_to_check = j + neighbor_cols[k];
                    if(row_to_check < 0 || row_to_check > 9 || col_to_check < 0 || col_to_check > 9)
                    {
                        continue;
                    }
                    else
                    {
                        try {
                            if(arr[row_to_check][col_to_check] === 'bomb')
                                {
                                    cnt_mines++;
                                }
                        } catch (error) {
                            //some NaN/undefined error we can ignore
                        }
                    }
                }
                arr[i][j] = cnt_mines;
            }
        }
    }
    return arr;
}

class Board extends React.Component {
    constructor(props) {
        super(props);

        var init = [];
        for(var i = 0; i < 9; i++)
        {
            init[i] = new Array(9);
        }
        init_board(init);

        this.state = {
            cells: init,
            init: init
        } /*would probably be wise to keep a copy of the initial 2D game board, hence the dupe.
        thinking this would help with doing the recursive reveals. */

        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                var val = init[k][j];
                init[k][j] = this.renderCell(k, j, val, null);//<Cell row={k} col={j} value={val} face={null}/>;
            }
        }

        this.setState({cells: init});
    }

    handleClick(row, col, value) {
        var revealed_cells = [];
        for(var i = 0; i < 9; i++)
        {
            revealed_cells[i] = new Array(9);
        }
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                if(k === row && j === col)
                {
                    var face = value;
                    if(face === 'bomb')
                    {
                        face = '*';
                    }
                    revealed_cells[k][j] = this.renderCell(row, col, value, face);
                }
                else
                {
                    revealed_cells[k][j] = this.state.cells[k][j];
                }
            }
        }

        this.setState({cells: revealed_cells});
    }

    renderCell(r, c, v, f) {
        return <Cell row={r} col={c} value={v} face={f} onClick={() => this.handleClick(r, c, v)}/>
    }

    render() {
        return (
            <div>
                <h2>Minesweeper.React</h2>
                <div className="grid">
                    {this.state.cells}
                </div>
            </div>
        )
    }
}

class Game extends React.Component {
    render() {
        return(
            <Board />
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
