import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={handleClick(this.props.key, this.props.row, this.props.col, this.props.value)}>
                {this.props.face}
            </button>
        );
    }
}

function init_board(arr) {
    var count = 0;
    while(count < 10)
    {
        var rand_r = Math.floor(Math.random() * 9);
        var rand_c = Math.floor(Math.random() * 9);
        while(arr[rand_r][rand_c] === 'bomb')
        {
            rand_r = Math.floor(Math.random() * 9);
            rand_c = Math.floor(Math.random() * 9);
        }
        arr[rand_r][rand_c] = 'bomb';
        count++;
    }

    var neighbor_rows = [-1, -1, -1, 0, 0,   1, 1, 1];
    var neighbor_cols = [-1,  0,  1, -1, 1, -1, 0, 1];

    for(var i = 0; i < 9; i++)
    {
        for(var j = 0; j < 9; j++)
        {
            if(arr[i][j] !== 'bomb')
            {
                var cnt_mines = 0;
                for(var k = 0; k < 9; k++)
                {
                    var row_to_check = i + neighbor_rows[k];
                    var col_to_check = j + neighbor_cols[k];
                    if(row_to_check < 0 || row_to_check > 9 || col_to_check < 0 || col_to_check > 9)
                    {
                        continue;
                    }
                    else
                    {
                        try {
                            if(arr[row_to_check][col_to_check] === 'bomb')
                                {
                                    cnt_mines++;
                                }
                        } catch (error) {
                            //some NaN/undefined error we can ignore
                        }
                    }
                }
                arr[i][j] = cnt_mines;
            }
        }
    }
    return arr;
}

function handleClick(key, row, col, value) {
    var revealed_cells = [];
    for(var i = 0; i < 9; i++)
    {
        revealed_cells[i] = new Array(9);
    }
    for(var k = 0; k < 9; k++)
    {
        for(var j = 0; j < 9; j++)
        {
            if(k === row && j === col)
            {
                var face = value;
                if(face === 'bomb')
                {
                    face = '*';
                }
                revealed_cells[k][j] = <Cell key={key} row={row} col={col} value={value} face={face}/>;
            }
            else
            {
                revealed_cells[k][j] = Board.state.cells[k][j];
            }
        }
    }

    Board.setState({cells: revealed_cells});
}

class Board extends React.Component {
    constructor(props) {
        super(props);

        var init = [];
        for(var i = 0; i < 9; i++)
        {
            init[i] = new Array(9);
        }
        init_board(init);
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                var val = init[k][j];
                init[k][j] = <Cell key={k + 1} row={k} col={j} value={val} face={null}/>;
            }
        }

        this.state = {
            cells: init,
        }
    }

    render() {

        // var init = [];
        // for(var i = 0; i < 9; i++)
        // {
        //     init[i] = new Array(9);
        // }
        // init_board(init);
        // for(var k = 0; k < 9; k++)
        // {
        //     for(var j = 0; j < 9; j++)
        //     {
        //         var val = init[k][j];
        //         init[k][j] = <Cell key={k + 1} row={k} col={j} value={val} face={null}/>;
        //     }
        // }

        return (
            <div>
                <h2>Minesweeper.React</h2>
                <div className="grid">
                    {this.state.cells}
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
