import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Cell extends React.Component {
    render() {
        return (
            <button className="cell" onClick={() => alert("" + this.props.row + ", " + this.props.col + " " + this.props.value)}/>
        );
    }
}

class Board extends React.Component {
    render() {
        var cells = [];
        for(var i = 0; i < 9; i++)
        {
            cells[i] = new Array(9);
        }
        for(var k = 0; k < 9; k++)
        {
            for(var j = 0; j < 9; j++)
            {
                cells[k][j] = <Cell row={k} col={j} value="null"/>;
            }
        }

        var count = 0;
        while(count < 9)
        {
            var rand_r = Math.floor(Math.random() * 10);
            var rand_c = Math.floor(Math.random() * 10)
            while(cells[rand_r][rand_c].props.value != "bomb") {
                rand_r = Math.floor(Math.random() * 10);
                rand_c = Math.floor(Math.random() * 10);
            }
            cells[rand_r][rand_c].value = 'bomb';
            count++;
        }

        return (
            <div>
                <h2>Minesweeper.React</h2>
                <div className="grid">
                    {cells}
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Board />,
    document.getElementById('root')
  );
